// app.js
import React, { useState } from "react";
import Dashboard from "./components/Dashboard";
import Alerts from "./components/Alerts"; // Assuming these exist or will be created
import Goals from "./components/Goals";   // Assuming these exist or will be created
import "./App.css";
import "./index.css";

function App() {
  const [latestData, setLatestData] = useState(null); // This state will now receive data from Dashboard

  return (
    <div className="App">
      <h1>Smart Home Savings</h1>
      <Dashboard setLatestData={setLatestData} />
      {/* You can use latestData here if Alerts or Goals need it */}
      <Alerts data={latestData} />
      <Goals />
    </div>
  );
}

export default App;
