import React, { useEffect, useState } from "react";
import { getLatestReading } from "../api";

const Dashboard = ({ setLatestData }) => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const result = await getLatestReading();
      if (result) {
        setData(result);
        if (setLatestData) setLatestData(result); // pass to parent for alerts
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 30000); // refresh every 30s
    return () => clearInterval(interval);
  }, [setLatestData]);

  if (!data) return <p>Loading latest readings...</p>;

  return (
    <div>
      <h2>Smart Home Dashboard</h2>
      <p>Temperature: {data.temperature} Â°C</p>
      <p>Humidity: {data.humidity} %</p>
      <p>Heating Cost: ${data.energy_cost}</p>
      <p>Mold Risk: ${data.mold_risk}</p>
    </div>
  );
};

export default Dashboard;
